	private static int geraCombinacoes(int tarefas, int maquinas) {
		int r = maquinas;
		return fact(tarefas) / (fact(r) *
                fact(tarefas - r));
	}
	
	static int fact(int n)
	{
	    int res = 1;
	    for (int i = 2; i <= n; i++)
	        res = res * i;
	    return res;
	}

	private static int[][] preencheArray(int[] input, int maq, int tarefas) {
		int[][] arrayTarMaq = new int[tarefas][maq];
		int contador1 = 0, contador2 = 0;
		for(int i = 0; i < input.length; i+=maq) {
			for(int t = 0; t< maq; t++) {
				arrayTarMaq[contador1][t] = input[contador2];
				contador2++;
			}
			contador1++;
			
		}
		
		return arrayTarMaq;
	}

