	private static int[] gerarSolucao(int tarefas, int maq) {
		int n1 = 0;
		Random rand1 = new Random();
		int[] solution = new int[tarefas];
		for(int k = 0; k < tarefas; k++) {
			//GERA UMA POSICAO ALEATORIA
			n1 = rand1.nextInt(maq) + 1;
			solution[k] = n1;
		}
		
		return solution;
	}

	private static int calculaMakespan(int[] solucao, int[] input, int maq, int tarefas) {
		int counter1 = 0, counter2 = 0;
		int[][] arrayMaqTar = new int[tarefas][maq];
		int[] counter = new int[maq];
		int[] tempMaq_1 = new int[tarefas], tempMaq_2 = new int[tarefas];
		//preencher array tempo tarefas para cada maquina
		int contador = 0;
		for(int t = 0; t < input.length; t+=maq) {
			for(int tt = 0; tt < maq; tt++) {
				arrayMaqTar[contador][tt] = input[t+tt];
			}
			contador++;
		}
		
//		System.out.println(Arrays.deepToString(arrayMaqTar));
		
//		for(int i = 0; i < input.length; i+=maq) {
//			tempMaq_1[contador] = input[i];
//			tempMaq_2[contador] = input[i+1];
//			contador++;
//		}
		
		for(int y = 0; y < solucao.length; y++) {
			if(solucao[y] == 1) {
				counter[0] += arrayMaqTar[y][0];
			}else if(solucao[y] == 2) {
				counter[1] += arrayMaqTar[y][1];
			}else if(solucao[y] == 3) {
				counter[2] += arrayMaqTar[y][2];
			}else if(solucao[y] == 4) {
				counter[3] += arrayMaqTar[y][3];
			}else if(solucao[y] == 5) {
				counter[4] += arrayMaqTar[y][4];
			}else if(solucao[y] == 6) {
				counter[5] += arrayMaqTar[y][5];
			}else if(solucao[y] == 7) {
				counter[6] += arrayMaqTar[y][6];
			}else if(solucao[y] == 8) {
				counter[7] += arrayMaqTar[y][7];
			}else if(solucao[y] == 9) {
				counter[8] += arrayMaqTar[y][8];
			}else if(solucao[y] == 10) {
				counter[9] += arrayMaqTar[y][9];
			}	
		}
		
		int max = counter[0];
		for(int u = 0; u < counter.length; u++) {
			if(counter[u] > max) {
				max = counter[u];
			}
		}
		
		return max;		
	}

